import React, { useState } from 'react';

function App() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [highlightedText, setHighlightedText] = useState(null);

  const sampleText = `Hello,\nMy name is Susan. I'm forteen and I life in Germany. My hobbys are go to discos, sometimes I hear music in the radio. In the summer I go bathing in a lake. I haven't any brothers or sisters. We take busses to scool. I visit year 9 at my school. My birthday is on Friday. I hope I will become a new guitar.\nI'm looking forward to get a e-mail from you.\n\nYours,\nSusan`;

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://127.0.0.1:5000/grammar-check', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);
    setHighlightedText(renderHighlightedText(text, result.matches || []));
  };

  const renderHighlightedText = (text, corrections) => {
    if (corrections.length === 0) return text;

    let processedText = [];
    let lastIndex = 0;

    corrections.forEach((match, index) => {
      const start = match.offset;
      const end = start + match.length;

      if (start > lastIndex) {
        processedText.push(text.slice(lastIndex, start));
      }

      processedText.push(
        <span
          key={index}
          style={{ textDecoration: 'underline', color: 'purple' }}
        >
          {text.slice(start, end)}
        </span>
      );

      lastIndex = end;
    });

    if (lastIndex < text.length) {
      processedText.push(text.slice(lastIndex));
    }

    return processedText;
  };

  return (
    <div className="App bg-indigo-900 pt-12 pr-6 pl-6 pb-6 md:p-10 lg:p-14 xl:p-16 2xl:p-16">
      <div className="flex flex-col md:flex-row">
        {/* Left Column (Grammar Correction Tool and Your Text + Highlighted Issues) */}
        <div className="w-full md:w-2/3 flex flex-col">
          {/* Grammar Correction Tool in the first row */}
          <div className="mb-8 md:mb-10 lg:mb-12 xl:mb-12 2xl:mb-12">
            <p className="font-extrabold mb-4 md:mb-5 lg:mb-6 xl:mb-6 2xl:mb-6 text-white text-3xl md:text-4xl lg:text-5xl xl:text-5xl 2xl:text-5xl">
              Grammar Correction Tool
            </p>
          </div>

          {/* Your Text and Highlighted Issues in the second row */}
          <div className="flex flex-col md:flex-row mb-8 md:mb-0">
            {/* Your Text */}
            <div className="w-full md:w-1/2 mb-8 md:mb-0 md:pr-4">
              <p className="font-bold mb-2 md:mb-3 lg:mb-4 xl:mb-4 2xl:mb-4 text-white text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
                Your text
              </p>
              <form onSubmit={handleSubmit}>
                <textarea
                  value={text}
                  onChange={handleTextChange}
                  placeholder="Enter your text here"
                  rows="12"
                  cols="50"
                  className="w-full shadow-lg rounded-lg p-4 mb-2 md:mb-3 lg:mb-3 xl:mb-3 2xl:mb-3"
                />
                <button
                  type="submit"
                  className="w-full shadow-lg mb-10 md:mb-12 font-medium text-white px-5 py-3 bg-indigo-500 rounded-full"
                >
                  Check Grammar
                </button>
              </form>
            </div>

            {/* Highlighted Issues */}
            <div className="w-full md:w-1/2">
              <p className="font-bold mb-2 md:mb-3 lg:mb-4 xl:mb-4 2xl:mb-4 text-white text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
                Highlighted issues
              </p>
              {highlightedText && (
                <div className="w-full shadow-lg rounded-lg p-4 bg-white mb-10 md:mb-12">
                  {highlightedText}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Right Column (Corrections with full height) */}
        <div className="w-full md:w-1/3 flex flex-col justify-start">
          <p className="font-bold mb-4 md:mb-5 lg:mb-6 xl:mb-6 2xl:mb-6 text-white text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
            Corrections
          </p>
          <div className="flex-grow overflow-auto bg-gray-800 p-4 rounded-lg">
            {corrections && corrections.length > 0 ? (
              <ul className="text-white">
                {corrections.map((match, index) => (
                  <li key={index}>
                    <strong>Issue:</strong> {match.message} <br />
                    <strong>Suggestion:</strong>{' '}
                    {match.replacements.map((r) => r.value).join(', ')}
                    <br />
                    <strong>Word at Offset:</strong>{' '}
                    {text.substring(match.offset, match.offset + match.length)}
                  </li>
                ))}
              </ul>
            ) : (
              <p>No corrections found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
