import React, { useState, useRef } from "react";

function App() {
  const [text, setText] = useState("");
  const [corrections, setCorrections] = useState(null);
  const [correctedText, setCorrectedText] = useState("");
  const editableDivRef = useRef(null);

  const handleTextChange = (event) => {
    setText(event.target.innerText);
  };

  const applyCorrections = (text, corrections) => {
    let newText = text;
    corrections
      .slice()
      .reverse()
      .forEach((match) => {
        const replacement = match.replacements.length > 0 ? match.replacements[0].value : match.context.text;
        newText =
          newText.slice(0, match.offset) +
          replacement +
          newText.slice(match.offset + match.length);
      });
    return newText;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/grammar-check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);

    // Apply corrections to the text
    const corrected = applyCorrections(text, result.matches || []);
    setCorrectedText(corrected);
  };

  return (
    <div className="App">
      <h1>Grammar Correction Tool</h1>
      <form onSubmit={handleSubmit}>
        <div
          ref={editableDivRef}
          spellCheck={false}
          contentEditable
          className="editable-div"
          onInput={handleTextChange}
          placeholder="Enter your text here"
          style={{
            width: "400px",
            height: "150px",
            border: "1px solid #ccc",
            padding: "10px",
            fontSize: "16px",
            overflow: "auto",
          }}
        />
        <br />
        <button type="submit">Check Grammar</button>
      </form>

      <h2>Corrections</h2>
      {corrections && corrections.length > 0 ? (
        <ul>
          {corrections.map((match, index) => (
            <li key={index}>
              <strong>Issue:</strong> {match.message} <br />
              <strong>Suggestion:</strong>{" "}
              {match.replacements.map((r) => r.value).join(", ")}
              <br />
              <strong>Offset:</strong> {match.offset}
              <br />
              <strong>Length:</strong> {match.length}
              <br />
              <strong>Word at Offset:</strong>{" "}
              {text.substring(match.offset, match.offset + match.length)}
            </li>
          ))}
        </ul>
      ) : (
        <p>No corrections found</p>
      )}

      <h2>Corrected Text</h2>
      <div
        style={{
          whiteSpace: "pre-wrap",
          border: "1px solid #ccc",
          padding: "10px",
          width: "400px",
          minHeight: "150px",
        }}
      >
        {correctedText || "No corrections applied yet"}
      </div>
    </div>
  );
}

export default App;
