import React, { useState, useRef, useEffect } from "react";

function App() {
  const [text, setText] = useState("");
  const [corrections, setCorrections] = useState(null);
  const editableDivRef = useRef(null);
  const [highlightedText, setHighlightedText] = useState("");

  const handleTextChange = (event) => {
    const newText = event.target.innerText;
    setText(newText);
    // Remove underline when the user types in the word with the <u> tag
    if (highlightedText) {
      const cleanText = editableDivRef.current.innerHTML.replace(/<\/?u>/g, "");
      setHighlightedText("");
      setText(cleanText);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/grammar-check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);

    // Highlight the word at the offset
    if (result.matches && result.matches.length > 0) {
      const match = result.matches[0];
      const wordToHighlight = text.substring(match.offset, match.offset + match.length);
      const newHighlightedText = `${text.substring(0, match.offset)}<u>${wordToHighlight}</u>${text.substring(match.offset + match.length)}`;
      setHighlightedText(newHighlightedText);
    }
  };

  useEffect(() => {
    // Update the contentEditable div with the highlighted text
    if (highlightedText) {
      editableDivRef.current.innerHTML = highlightedText;
    } else {
      editableDivRef.current.innerText = text;
    }
  }, [highlightedText, text]);

  return (
    <div className="App">
      <h1>Grammar Correction Tool</h1>
      <form onSubmit={handleSubmit}>
        <div
          ref={editableDivRef}
          spellCheck={false}
          contentEditable
          className="editable-div"
          onInput={handleTextChange}
          placeholder="Enter your text here"
          style={{
            width: "400px",
            height: "150px",
            border: "1px solid #ccc",
            padding: "10px",
            fontSize: "16px",
            overflow: "auto"
          }}
        />
        <br />
        <button type="submit">Check Grammar</button>
      </form>

      <h2>Corrections</h2>
      {corrections && corrections.length > 0 ? (
        <ul>
          {corrections.map((match, index) => (
            <li key={index}>
              <strong>Issue:</strong> {match.message} <br />
              <strong>Suggestion:</strong> {match.replacements.map(r => r.value).join(", ")}
              <br /><strong>Offset:</strong> {match.offset}
              <br /><strong>Length:</strong> {match.length}
              <br /><strong>Word at Offset:</strong> {text.substring(match.offset, match.offset + match.length)}
            </li>
          ))}
        </ul>
      ) : (
        <p>No corrections found</p>
      )}
    </div>
  );
}

export default App;
