import React, { useState, useRef, useEffect } from "react";

function App() {
  const [text, setText] = useState("");
  const [corrections, setCorrections] = useState([]);
  const contentEditableRef = useRef(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleContentEditableChange = () => {
    const rawText = contentEditableRef.current.innerText;
    setText(rawText);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/grammar-check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);
  };

  const renderHighlightedText = () => {
    let highlightedText = "";
    let lastIndex = 0;

    if (corrections.length === 0) {
      return text;
    }

    corrections.forEach((match, index) => {
      const start = match.offset;
      const end = start + match.length;

      // Add the text before the issue
      highlightedText += text.slice(lastIndex, start);

      // Add the underlined part
      highlightedText += `<span style="text-decoration: underline; color: red;">${text.slice(
        start,
        end
      )}</span>`;

      lastIndex = end;
    });

    // Add remaining text after the last issue
    if (lastIndex < text.length) {
      highlightedText += text.slice(lastIndex);
    }

    return highlightedText;
  };

  // Update the contentEditable div with the highlighted text
  useEffect(() => {
    if (contentEditableRef.current) {
      contentEditableRef.current.innerHTML = renderHighlightedText();
    }
  }, [corrections, text]);

  return (
    <div className="App">
      <h1>Grammar Correction Tool</h1>
      <form onSubmit={handleSubmit}>
        <div
          ref={contentEditableRef}
          contentEditable
          style={{
            width: "500px",
            height: "200px",
            border: "1px solid black",
            padding: "10px",
            whiteSpace: "pre-wrap",
            overflowWrap: "break-word",
          }}
          onInput={handleContentEditableChange}
        ></div>

        <textarea
          value={text}
          onChange={handleTextChange}
          style={{ display: "none" }} // Hidden but still used to store the real text
        />
        <br />
        <button type="submit">Check Grammar</button>
      </form>

      <h2>Corrections</h2>
      {corrections && corrections.length > 0 ? (
        <ul>
          {corrections.map((match, index) => (
            <li key={index}>
              <strong>Issue:</strong> {match.message} <br />
              <strong>Suggestion:</strong>{" "}
              {match.replacements.map((r) => r.value).join(", ")}
              <br />
              <strong>Offset:</strong> {match.offset}
            </li>
          ))}
        </ul>
      ) : (
        <p>No corrections found</p>
      )}
    </div>
  );
}

export default App;
