import React, { useState, useEffect, useRef } from "react";

function App() {
  const [text, setText] = useState("");
  const [corrections, setCorrections] = useState(null);
  const editableDivRef = useRef(null); // To directly manipulate the contentEditable div

  const handleTextChange = (event) => {
    // Strip out any <u> tags as the user types
    const newText = event.target.innerHTML.replace(/<u>|<\/u>/g, "");
    setText(newText);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/grammar-check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);
  };

  useEffect(() => {
    if (corrections && corrections.length > 0) {
      underlineWordsAtOffsets();
    }
  }, [corrections]);

  const underlineWordsAtOffsets = () => {
    let updatedText = text;
    let offsetCorrection = 0;

    corrections.forEach((match) => {
      const { offset, length } = match;
      const wordToUnderline = text.substring(offset, offset + length);

      // Only underline words that are still unchanged
      const regex = new RegExp(`\\b${wordToUnderline}\\b`, "g");
      if (regex.test(text)) {
        const underlinedWord = `<u>${wordToUnderline}</u>`;

        updatedText =
          updatedText.slice(0, offset + offsetCorrection) +
          underlinedWord +
          updatedText.slice(offset + length + offsetCorrection);

        offsetCorrection += "<u></u>".length;
      }
    });

    // Set the innerHTML of the editable div
    if (editableDivRef.current) {
      editableDivRef.current.innerHTML = updatedText;
    }
  };

  return (
    <div className="App">
      <h1>Grammar Correction Tool</h1>
      <form onSubmit={handleSubmit}>
        <div
          spellCheck={false}
          contentEditable
          className="editable-div"
          onInput={handleTextChange}
          ref={editableDivRef} // Use ref to directly manipulate this div
          placeholder="Enter your text here"
          style={{
            width: "400px",
            height: "150px",
            border: "1px solid #ccc",
            padding: "10px",
            fontSize: "16px",
            overflow: "auto",
          }}
        />
        <br />
        <button type="submit">Check Grammar</button>
      </form>

      <h2>Corrections</h2>
      {corrections && corrections.length > 0 ? (
        <ul>
          {corrections.map((match, index) => (
            <li key={index}>
              <strong>Issue:</strong> {match.message} <br />
              <strong>Suggestion:</strong> {match.replacements.map((r) => r.value).join(", ")}
              <br />
              <strong>Offset:</strong> {match.offset}
              <br />
              <strong>Length:</strong> {match.length}
              <br />
              <strong>Word at Offset:</strong>{" "}
              {text.substring(match.offset, match.offset + match.length)}
            </li>
          ))}
        </ul>
      ) : (
        <p>No corrections found</p>
      )}
    </div>
  );
}

export default App;
