import React, { useState, useRef, useEffect } from "react";

function App() {
  const [text, setText] = useState("");
  const [corrections, setCorrections] = useState([]);
  const editableDivRef = useRef(null);

  const handleTextChange = (event) => {
    setText(event.target.innerText);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/grammar-check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);
  };

  // Function to highlight matches in the contenteditable div
  const highlightMatches = () => {
    const div = editableDivRef.current;
    let innerHTML = text;

    corrections.forEach((match) => {
      const wordToUnderline = text.substring(match.offset, match.offset + match.length);
      const regex = new RegExp(`\\b${wordToUnderline}\\b`, 'g');
      innerHTML = innerHTML.replace(regex, `<span class="underline">${wordToUnderline}</span>`);
    });

    div.innerHTML = innerHTML;
  };

  // Effect to highlight the matches when corrections are updated
  useEffect(() => {
    highlightMatches();
  }, [corrections, text]);

  // Function to handle user input and remove underline if they type in the underlined word
  const handleUserTyping = (event) => {
    const selection = window.getSelection();
    const range = selection.getRangeAt(0);
    const node = range.startContainer;

    if (node.parentNode && node.parentNode.classList.contains("underline")) {
      const newNode = document.createTextNode(node.textContent);
      node.parentNode.replaceWith(newNode);
    }
    setText(event.target.innerText);
  };

  return (
    <div className="App">
      <h1>Grammar Correction Tool</h1>
      <form onSubmit={handleSubmit}>
        <div
          ref={editableDivRef}
          spellCheck={false}
          contentEditable
          className="editable-div"
          onInput={handleUserTyping}
          placeholder="Enter your text here"
          style={{
            width: "400px",
            height: "150px",
            border: "1px solid #ccc",
            padding: "10px",
            fontSize: "16px",
            overflow: "auto",
          }}
        />
        <br />
        <button type="submit">Check Grammar</button>
      </form>

      <h2>Corrections</h2>
      {corrections && corrections.length > 0 ? (
        <ul>
          {corrections.map((match, index) => (
            <li key={index}>
              <strong>Issue:</strong> {match.message} <br />
              <strong>Suggestion:</strong> {match.replacements.map((r) => r.value).join(", ")}
              <br />
              <strong>Word at Offset:</strong> {text.substring(match.offset, match.offset + match.length)}
            </li>
          ))}
        </ul>
      ) : (
        <p>No corrections found</p>
      )}
    </div>
  );
}

export default App;
