import React, { useState } from 'react';

function App() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [highlightedText, setHighlightedText] = useState(null);
  const [hasSubmitted, setHasSubmitted] = useState(false); // Tracks if the grammar check button has been clicked
  const [isLoading, setIsLoading] = useState(false); // Loading state to handle the async request

  const sampleText = `Hello,\nMy name is Susan. I'm forteen and I life in Germany. My hobbys are go to discos, sometimes I hear music in the radio. In the summer I go bathing in a lake. I haven't any brothers or sisters. We take busses to scool. I visit year 9 at my school. My birthday is on Friday. I hope I will become a new guitar.\nI'm looking forward to get a e-mail from you.\n\nYours,\nSusan`;

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSampleTextInsert = () => {
    setText(sampleText);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true); // Start loading state
    setHasSubmitted(true); // Mark that the check grammar button has been clicked

    const response = await fetch('http://127.0.0.1:5000/grammar-check', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);

    // If no corrections, show "The text is correct!" in Highlighted Issues
    if (result.matches.length === 0) {
      setHighlightedText('The text is correct!');
    } else {
      setHighlightedText(renderHighlightedText(text, result.matches || []));
    }

    setIsLoading(false); // Stop loading state after receiving the result
  };

  const renderHighlightedText = (text, corrections) => {
    if (corrections.length === 0) return text;

    let processedText = [];
    let lastIndex = 0;

    corrections.forEach((match, index) => {
      const start = match.offset;
      const end = start + match.length;

      if (start > lastIndex) {
        processedText.push(text.slice(lastIndex, start));
      }

      processedText.push(
        <span
          key={index}
          style={{ textDecoration: 'underline', color: 'purple' }}
        >
          {text.slice(start, end)}
        </span>
      );

      lastIndex = end;
    });

    if (lastIndex < text.length) {
      processedText.push(text.slice(lastIndex));
    }

    return processedText;
  };

  return (
    <div className="App bg-indigo-900 md:h-screen lg:h-screen xl:h-screen">
      <div className="flex flex-col md:flex-row h-full overflow-hidden">
        {/* 1st Column: Grammar Correction Tool and Your Text */}
        <div className="w-full md:w-1/2 flex flex-col h-full pt-12 pr-6 pl-6 pb-6 md:p-10 lg:p-14 xl:p-16 2xl:p-16">
          {/* Grammar Correction Tool */}
          <div className="mb-8 md:mb-10 lg:mb-12 xl:mb-12 2xl:mb-12">
            <p className="font-extrabold mb-4 md:mb-5 lg:mb-6 xl:mb-6 2xl:mb-6 text-white text-3xl md:text-4xl lg:text-5xl xl:text-5xl 2xl:text-5xl">
              Grammar Correction Tool
            </p>
          </div>

          {/* Your Text */}
          <div className="mb-8 md:mb-10 lg:mb-12 xl:mb-12 2xl:mb-12 flex-grow">
            <p className="font-bold mb-2 md:mb-3 lg:mb-4 xl:mb-4 2xl:mb-4 text-white text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
              Your text
            </p>
            <div className="relative">
              <textarea
                value={text}
                onChange={handleTextChange}
                placeholder="Enter your text here"
                rows="12"
                cols="50"
                className="w-full shadow-lg rounded-lg p-4 mb-2 md:mb-3 lg:mb-3 xl:mb-3 2xl:mb-3 h-full"
              />
              {text === '' && (
                <button
                  onClick={handleSampleTextInsert}
                  className="absolute bottom-6 left-4 mb-2 shadow-lg font-medium text-white px-5 py-3 bg-indigo-400 rounded-full"
                >
                  Insert Sample Text
                </button>
              )}
            </div>
            <button
              onClick={handleSubmit}
              type="submit"
              className="w-full shadow-lg mb-10 md:mb-12 font-medium text-white px-5 py-3 bg-indigo-500 rounded-full"
            >
              Check Grammar
            </button>
          </div>
        </div>

        {/* 2nd Column: Highlighted Issues */}
        <div className="w-full md:w-1/4 flex flex-col overflow-auto">
          <p className="font-bold mb-2 md:mb-3 lg:mb-4 xl:mb-4 2xl:mb-4 text-white text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
            Highlighted issues
          </p>
          <div className="flex-grow overflow-auto shadow-lg rounded-lg p-4 bg-white">
            {/* Display loading message or highlighted text based on state */}
            {isLoading ? (
              <p>Checking text...</p>
            ) : !hasSubmitted ? (
              <p>Click the check grammar button</p>
            ) : highlightedText ? (
              <div>{highlightedText}</div>
            ) : (
              <p>The text is correct!</p>
            )}
          </div>
        </div>

        {/* 3rd Column: Corrections (Fixed height on mobile/tablet) */}
        <div className="w-full md:w-1/4 flex flex-col h-64 md:h-full overflow-auto">
          <p className="font-bold mb-2 md:mb-3 lg:mb-4 xl:mb-4 2xl:mb-4 text-white text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
            Corrections
          </p>
          <div className="flex-grow overflow-auto shadow-lg rounded-lg p-4 bg-white">
            {/* Display loading message or corrections based on state */}
            {isLoading ? (
              <p>Checking text...</p>
            ) : !hasSubmitted ? (
              <p>Time to check your text</p>
            ) : corrections.length > 0 ? (
              <div className="flex flex-col space-y-4">
                {corrections.map((match, index) => (
                  <div
                    key={index}
                    className="border-b bg-white p-4 shadow-sm rounded-lg"
                  >
                    <p className="font-bold text-indigo-600">
                      {text.substring(
                        match.offset,
                        match.offset + match.length
                      )}
                    </p>
                    <p className="text-gray-700">{match.message}</p>
                    <p className="text-green-600">
                      {match.replacements
                        .slice(0, 4)
                        .map((r) => r.value)
                        .join(', ')}
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <p>No corrections found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
