import React, { useState, useRef } from "react";
import "./App.css";

function App() {
  const [underlinedText, setUnderlinedText] = useState("");
  const textareaRef = useRef(null);

  const handleUnderline = () => {
    setUnderlinedText(textareaRef.current.value);
  };

  return (
    <div className="App">
      <textarea
        ref={textareaRef}
        className="textarea"
        placeholder="Type something here..."
      />
      <button onClick={handleUnderline}>Underline Text</button>

      {/* Display underlined text */}
      <div className="underlined-text">
        <u>{underlinedText}</u>
      </div>
    </div>
  );
}

export default App;
