import React, { useState } from 'react';

function App() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [highlightedText, setHighlightedText] = useState(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://127.0.0.1:5000/grammar-check', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);
    setHighlightedText(renderHighlightedText(text, result.matches || []));
  };

  const renderHighlightedText = (text, corrections) => {
    if (corrections.length === 0) return text;

    let processedText = [];
    let lastIndex = 0;

    corrections.forEach((match, index) => {
      const start = match.offset;
      const end = start + match.length;

      if (start > lastIndex) {
        processedText.push(text.slice(lastIndex, start));
      }

      processedText.push(
        <span key={index} style={{ textDecoration: 'underline', color: 'red' }}>
          {text.slice(start, end)}
        </span>
      );

      lastIndex = end;
    });

    if (lastIndex < text.length) {
      processedText.push(text.slice(lastIndex));
    }

    return processedText;
  };

  return (
    <div className="App">
      <p className="font-extrabold text-3xl md:text-4xl lg:text-5xl xl:text-5xl 2xl:text-5xl">
        Grammar Correction Tool
      </p>
      <div className="flex flex-col md:flex-row">
        <div className="w-full md:w-1/3 p-4">
          <p className="font-bold text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
            Your text
          </p>
          <form onSubmit={handleSubmit}>
            <textarea
              value={text}
              onChange={handleTextChange}
              placeholder="Enter your text here"
              rows="10"
              cols="50"
            />
            <br />
            <button type="submit">Check Grammar</button>
          </form>
        </div>
        <div className="w-full md:w-1/3 p-4">
          {highlightedText && (
            <>
              <p className="font-bold text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
                Text with Issues Highlighted
              </p>
              <div>{highlightedText}</div>
            </>
          )}
        </div>
        <div className="w-full md:w-1/3 p-4">
          <p className="font-bold text-2xl md:text-2xl lg:text-3xl xl:text-3xl 2xl:text-3xl">
            Corrections
          </p>
          {corrections && corrections.length > 0 ? (
            <ul>
              {corrections.map((match, index) => (
                <li key={index}>
                  <strong>Issue:</strong> {match.message} <br />
                  <strong>Suggestion:</strong>{' '}
                  {match.replacements.map((r) => r.value).join(', ')}
                  <br />
                  <strong>Word at Offset:</strong>{' '}
                  {text.substring(match.offset, match.offset + match.length)}
                </li>
              ))}
            </ul>
          ) : (
            <p>No corrections found</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
