import React, { useState } from "react";

function App() {
  const [text, setText] = useState("");
  const [corrections, setCorrections] = useState(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/grammar-check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    setCorrections(result.matches || []);
  };

  // Function to generate the text with underlined issues
  const getHighlightedText = () => {
    if (!corrections || corrections.length === 0) {
      return text; // No corrections, return original text
    }

    let newText = [];
    let lastIndex = 0;

    corrections.forEach((match, index) => {
      const { offset, length } = match;

      // Push the text before the error
      if (lastIndex < offset) {
        newText.push(text.slice(lastIndex, offset));
      }

      // Push the underlined text that contains the error
      newText.push(
        <span key={index} style={{ textDecoration: "underline", color: "red" }}>
          {text.slice(offset, offset + length)}
        </span>
      );

      // Update lastIndex to the end of the current match
      lastIndex = offset + length;
    });

    // Add any remaining text after the last correction
    if (lastIndex < text.length) {
      newText.push(text.slice(lastIndex));
    }

    return newText;
  };

  return (
    <div className="App">
      <h1>Grammar Correction Tool</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={text}
          onChange={handleTextChange}
          placeholder="Enter your text here"
          rows="10"
          cols="50"
        />
        <br />
        <button type="submit">Check Grammar</button>
      </form>

      <h2>Corrections</h2>
      {corrections && corrections.length > 0 ? (
        <div>
          <h3>Highlighted Issues:</h3>
          <p>{getHighlightedText()}</p> {/* Render the highlighted text */}
          <ul>
            {corrections.map((match, index) => (
              <li key={index}>
                <strong>Issue:</strong> {match.message} <br />
                <strong>Suggestion:</strong> {match.replacements
                  .map((r) => r.value)
                  .join(", ")}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No corrections found</p>
      )}
    </div>
  );
}

export default App;
