import React, { useState, useRef } from "react";

function App() {
  const [text, setText] = useState("");
  const [corrections, setCorrections] = useState(null);
  const editableDivRef = useRef(null);

  const handleTextChange = (event) => {
    setText(event.target.innerText);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/grammar-check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text }),
    });

    const result = await response.json();
    const correctedText = applyCorrections(text, result.matches || []);
    setText(correctedText);  // Update text with underlines
    setCorrections(result.matches || []);
  };

  const applyCorrections = (text, matches) => {
    let newText = text;

    // Sort corrections by offset to apply them in reverse order to avoid index shifting
    matches.sort((a, b) => b.offset - a.offset);

    matches.forEach((match) => {
      const start = match.offset;
      const end = start + match.length;
      const wordToUnderline = newText.substring(start, end);

      // Wrap the word with <u> tag to underline it
      newText = newText.slice(0, start) + `<u>${wordToUnderline}</u>` + newText.slice(end);
    });

    return newText;
  };

  return (
    <div className="App">
      <h1>Grammar Correction Tool</h1>
      <form onSubmit={handleSubmit}>
        <div
          ref={editableDivRef}
          spellCheck={false}
          contentEditable
          className="editable-div"
          onInput={handleTextChange}
          dangerouslySetInnerHTML={{ __html: text }} // Render the text with underlines
          placeholder="Enter your text here"
          style={{
            width: "400px",
            height: "150px",
            border: "1px solid #ccc",
            padding: "10px",
            fontSize: "16px",
            overflow: "auto"
          }}
        />
        <br />
        <button type="submit">Check Grammar</button>
      </form>

      <h2>Corrections</h2>
      {corrections && corrections.length > 0 ? (
        <ul>
          {corrections.map((match, index) => (
            <li key={index}>
              <strong>Issue:</strong> {match.message} <br />
              <strong>Suggestion:</strong> {match.replacements.map(r => r.value).join(", ")}
              <br /><strong>Offset:</strong> {match.offset}
              <br /><strong>Length:</strong> {match.length}
              <br /><strong>Word at Offset:</strong> {text.substring(match.offset, match.offset + match.length)}
            </li>
          ))}
        </ul>
      ) : (
        <p>No corrections found</p>
      )}
    </div>
  );
}

export default App;
