import React, { useRef } from "react";
import "./App.css";

function App() {
  const textareaRef = useRef(null);

  const handleHighlight = () => {
    textareaRef.current.select();
  };

  return (
    <div className="App">
      <textarea
        ref={textareaRef}
        className="textarea"
        placeholder="Type something here..."
      />
      <button onClick={handleHighlight}>Highlight Text</button>
    </div>
  );
}

export default App;
